import React from 'react';
const Call = (props) => {
  if(props.loading){
    document.getElementsByClassName('desc')[0].style.display ="";
  return(
    <div className="desc">
    <div className="desc-head">
      {props.description.name} - {props.description.symbol}
    </div>
    <div className="desc-body-left">
      <div><img src={props.description.image.large}/></div>
    </div>
    <div className="desc-body-right">
      {props.description.description.en}
    </div>
    </div>
  )
}
else{
  return(<div></div>)
}
}
export default Call;
