import React, { Component } from 'react';
import {BrowserRouter as Router,Link} from 'react-router-dom';
import Route from 'react-router-dom/Route';
import Call from './Call';
class App extends Component {
  constructor(){
    super();
    this.state = {
      isLoad : false,
      items: []
    }
  }
   componentDidMount(){
    fetch("https://api.coingecko.com/api/v3/coins/list")
    .then(res => res.json())
    .then((result) => {
      this.setState({isLoad : true});
    })
  }
  fun = ()=>{
    let a = document.getElementsByTagName('input')[0].value;
    fetch("https://api.coingecko.com/api/v3/coins/"+a)
    .then(res => res.json())
    .then((result) => {
      this.setState({items : result},()=> console.log(this.state.items));
    })

  }
  render() {
    const {isLoad,items} = this.state;
    if(!isLoad){
      return(
        <div>
        <h1>Crypto Wiki</h1>
        <h3>Loading The Coin List</h3>
        </div>
      )
    }
    else{
    return (
      <Router>
      <div>
      <h1>Crypto Wiki</h1>
      <input type="text" placeholder="Enter the Crypto Currency Name"/>
      <button onClick={this.fun}>Get Info About Coin</button><br/>
      <p>Search For a Coin</p>
      </div>
      </Router>
    );
  }
  }
}
export default App;
